    var sock, // socket SockJS
        RECONNECT_DELAY,
        reconnectAttempt,
        reconnect;

    function broadcast(messages) {
        var i, message;
        for (i = 0; i < messages.length; i++) {
            message = messages[i];
            Backbone.trigger(message.type.toLowerCase() + ':alert', message);
        }
    }

